// src/components/Education.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../index.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretRight } from '@fortawesome/free-solid-svg-icons';
import edubg from '../assets/images/edubg.jpg';
const BASE_URL = process.env.REACT_APP_API_BASE;

const Education = () => {
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    axios.get(`${BASE_URL}/api/profile/`)
      .then(res => {
        setProfile(res.data[0]);
      })
      .catch(err => {
        console.error('Error fetching profile:', err);
      });
  }, []);

  if (!profile) return <p>Loading...</p>;

  return (
    <section id="education" className="mt-64">
      <FontAwesomeIcon icon={faCaretRight} style={{ color: "#FFD43B" }} className="ml-40"/>
      <h7 className="text-white font-instrument ml-6 text-[14px] font-semibold">EDUCATION</h7>
      <div className="relative w-full">
  {/* Background Image (di belakang) */}
  <img
    src={edubg}
    alt="Background"
    className="absolute  w-[800px] mt-12 z-0"
  />

  {/* Konten lain (di atas background) */}
  <div className="relative z-10 flex flex-col items-center mt-0 space-y-4">
    <img src="/assets/images/logoTelkom.jpg" alt="Logo Telkom" className="w-64 h-40" />
    <h4 className="text-white text-xl">{profile.college}</h4>
    <p className="text-white">{profile.bachelor}</p>
    <p className="text-white">{profile.startYear} - {profile.endYear}</p>
    <img src="/assets/images/tult.jpg" alt="TULT" className="w-64 h-40" />
  </div>
</div>
    </section>
  );
};

export default Education;
