// src/components/Experience.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Experience = () => {
  const [experienceList, setExperienceList] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/api/experience/')
      .then(res => {
        setExperienceList(res.data);
      })
      .catch(err => {
        console.error('Error fetching experience data:', err);
      });
  }, []);

  return (
    <section id="experience" className="p-6">
      <h2 className="text-2xl font-bold mb-4">Experience</h2>
      {experienceList.length === 0 ? (
        <p>Loading...</p>
      ) : (
        <ul className="space-y-4">
          {experienceList.map((exp) => (
            <li key={exp._id} className="bg-gray-100 p-4 rounded-xl shadow">
              <h3 className="text-xl font-semibold">{exp.position} <span className="text-sm text-gray-600">({exp.status})</span></h3>
              <p className="text-sm text-gray-700">{exp.organization}</p>
              <p className="text-sm text-gray-500">{exp.startYear} - {exp.endYear}</p>
              <p className="mt-2 text-gray-800">{exp.description}</p>
              <a href="#evidence">Evidence</a>
            </li>
          ))}
        </ul>
      )}
    </section>
  );
};

export default Experience;
