// src/components/Experience.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../index.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretRight } from '@fortawesome/free-solid-svg-icons';
import expbg from '../assets/images/expbg.jpg';
const BASE_URL = process.env.REACT_APP_API_BASE;

const Experience = () => {
  const [experienceList, setExperienceList] = useState([]);

  useEffect(() => {
    axios.get(`${BASE_URL}/api/experience/`)
      .then(res => {
        setExperienceList(res.data);
      })
      .catch(err => {
        console.error('Error fetching experience data:', err);
      });
  }, []);

  return (
    <section id="experience" className='mt-64'>
      <FontAwesomeIcon icon={faCaretRight} style={{ color: "#FFD43B" }} className="ml-40"/>
      <h7 className="text-white font-instrument ml-6 text-[14px] font-semibold">EXPERIENCE</h7>
      <div className="relative w-full">
        {/* Background Image (di belakang) */}
        <img
          src={expbg}
          alt="Background"
          className="absolute top-0 left-0 w-full h-[300px] object-cover z-0 mt-0 outline outline-6 outline-blue"
        />

        {/* Konten lain (di atas background) */}
        <div className="relative z-10 flex flex-wrap justify-center mt-20 gap-24">
        {experienceList.length === 0 ? (
          <p>Loading...</p>
        ) : (
          <ul className="lex flex-wrap">
            {experienceList.map((exp) => (
              <li key={exp._id} className="group p-4 rounded-md hover:bg-gray-100 transition">
                {/* Selalu terlihat */}
                <h3 className="font-semibold">
                  {exp.startYear} - {exp.endYear}, {exp.position}
                  <span className="text-gray-500"> ({exp.status})</span>
                </h3>

                {/* Disembunyikan dan hanya tampil saat hover */}
                <div className="hidden group-hover:block mt-2 text-sm text-gray-700">
                  <p><strong>Organization:</strong> {exp.organization}</p>
                  <p><strong>Description:</strong> {exp.description}</p>
                  <a href="#evidence" className="text-blue-500 hover:underline">Evidence</a>
                </div>
              </li>
            ))}
          </ul>
        )}
        </div>
      </div>
    </section>
  );
};

export default Experience;
