import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../index.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretRight } from '@fortawesome/free-solid-svg-icons';

const BASE_URL = process.env.REACT_APP_API_BASE;

const Projects = () => {
  const [projectsList, setProjectsList] = useState([]);

  useEffect(() => {
    axios.get(`${BASE_URL}/api/projects/`)
      .then(res => {
        setProjectsList(res.data);
      })
      .catch(err => {
        console.error('Error fetching projects data:', err);
      });
  }, []);

  // Ambil hanya satu project dan tampilkan sisa dengan "<...>"
  const firstProject = projectsList[0]; // Project pertama
  const remainingProjects = projectsList.slice(1); // Sisa project lainnya

  return (
    <section id="projects" className='mt-72'>
      <FontAwesomeIcon icon={faCaretRight} style={{ color: "#FFD43B" }} className="ml-40"/>
      <h7 className="text-white font-instrument ml-6 text-[14px] font-semibold">PROJECTS</h7>
      <h2>Projects</h2>

      {/* Tampilkan hanya 1 project */}
      {firstProject ? (
        <div>
          <img src="/assets/images/prjprvw.jpg" alt="Preview" width="300" height="200" />
          <p>{firstProject.type}</p>
          <h1>{firstProject.title}: {firstProject.motto}</h1>
          <p>As {firstProject.position}</p>
          <p>{firstProject.description}</p>

          <div>
            <strong>Technologies:</strong>
            <ul>
              {firstProject.technologies.map((tech, idx) => (
                <li key={idx}>{tech}</li>
              ))}
            </ul>
          </div>

          <div>
            <strong>Links:</strong>
            <ul>
              {firstProject.link.map((url, idx) => (
                <li key={idx}>
                  <a href={url} target="_blank" rel="noopener noreferrer">{url}</a>
                </li>
              ))}
            </ul>
          </div>

          <a href="#coba">Try it!</a>
        </div>
      ) : (
        <p>Loading...</p>
      )}

      {/* Tampilkan "<...>" untuk project lainnya */}
      {remainingProjects.length > 0 && <p>...and more projects</p>}
    </section>
  );
};

export default Projects;
